import React from "react";
import { CardPanel } from "react-materialize";
import { useParams } from "react-router-dom";

import "./productForm.css";
import products from "../../constants/products.js";


const ProductForm = props => {
    
let { url } = useParams();
const test = 0;

const currentTest = () => {
    for (let i =0; i < products.products.length; i++) {
        if (url === products.products[i].name) {
            test = i;
            console.log("this worked!" + products.products[i].name + "array of" + test);
        }
    }
    return test; 
}




    const Radios = () => {
    
        currentTest();
        console.log("this is test/ array number" + test)
        let currentProduct = products.products[{test}].map((item) => {
        console.log("new line" + item);
        return (
            <div className="customRadio">
                <CardPanel>
                <input
                    type="radio" name="product"
                    id={products.products.pos.name} value="test" />
                <label for='test'>
                    <img className="radioImage"
                        src="//placekitten.com/151/151"
                        alt="I'm happy" />
                </label>
                <p>{item}</p>
            </CardPanel>
            </div>
            );
    });
        return currentProduct;
    };

    // use state once submit his hit, to slide in the second set of questions

    return (
        // <CardPanel className="formCard">
        <div id="radioDiv">
            <Radios />
        </div>
        // </CardPanel>
    )
};

export default ProductForm;
