import React, { useState } from "react";
import { Navbar, NavItem, Icon, TextInput, CardPanel } from "react-materialize";
import "./header.css";

const Header = props => {

    const [searchDrop, setSearchDrop] = useState(false);

    // const searchStyle = {
    //     search: {
    //         width: '80%',
    //         marginLeft: '10%'
    //     }
    // }

    let searchClass = "hideSearch scale-transition scale-out"

    const SearchDropDown = () => {
        return (
            <div 
            // style={searchStyle.search}
            className={searchClass}
            >
                <CardPanel>
                    <TextInput
                        icon="search"
                        placeholder="Search"
                    />
                </CardPanel>
            </div>
        )
    };

    const searchSwitch = () => {
        if (!searchDrop) {
            setSearchDrop(true);
        } else {
            setSearchDrop(false);
        }
    }

    if (!searchDrop) {
        // searchStyle.search.visibility = "hidden";
        searchClass = "hideSearch scale-transition scale-out";
    } else {
        // searchStyle.search.visibility = "visible";
        searchClass = "showSearch scale-transition scale-in";
    }


    return (
        <div>
            <Navbar
                alignLinks="right"
                brand={<a
                    className="brand-logo" href="#">Name Goes Here</a>}
                menuIcons={<Icon>menu</Icon>}
                options={{
                    draggable: true,
                    edge: "left",
                    inDuration: 250,
                    onCloseEnd: null,
                    onCloseStart: null,
                    onOpenEnd: null,
                    onOpenStart: null,
                    outDuration: 200,
                    preventScrolling: true
                }}
            >
                <NavItem href="#">
                    Getting started
            </NavItem>
                <NavItem href="#">
                    Categories
            </NavItem>
                <NavItem style={{ marginRight: "15%" }} href="#"
                    onClick={() => searchSwitch()}
                >
                    <Icon>search</Icon>
                </NavItem>
            </Navbar>
            <SearchDropDown />
        </div>
    )
};

// const styles = {
//     navLogo: {
//         marginLeft: '2%'
//     }
// }

export default Header;
