    }
}

const SearchDropDown = () => {
    return(
    <div style={searchStyle.search}> 
        <TextInput
        icon="search"
        placeholder="Search"
        />
    </div>
)};

const searchSwitch = () => {
    if (!searchDrop) {
        setSearchDrop(true);
    } else {
        setSearchDrop(false);
    }
}

if (!searchDrop) {
    searchStyle.search.visibility = "hidden";
} else {
    searchStyle.search.visibility = "visible";
}


