import React from "react";
import { Formik, Field, Form, useField } from "formik";
import { TextInput, Button, Select, CardPanel, Icon, RadioGroup } from "react-materialize";

import "./leadForm.css";

const MyRadio = ({ ...props}) => {
  const [field] = useField(props);
  return(
    <RadioGroup label="demo" name="demo"
      options={[
        {
          label: 'Yes',
          value: "yes"
        },
        {
          label: "No",
          value: "no"
        }
      ]}
      withGap
      {...field}
      radioClassNames="radioClass"
      required
    />
  )
}



const MyTextInput = ({label, name, type, ...props}) => {
  const [field, meta] = useField(props);
  let errorName = "errors." + name;
  return <TextInput className="validate" type={type} label={label} {...field} validate  />
};


const MySelect = ({ ...props}) => {
  const [field] = useField(props);
  return(
      <Select
                // label="state"
                field {...field}
                options={{
                    classes: "validate",
                    dropdownOptions: {
                        alignment: "right",
                        autoTrigger: true,
                        closeOnClick: true,
                        constrainWidth: true,
                        coverTrigger: true,
                        hover: false,
                        inDuration: 150,
                        onCloseEnd: null,
                        onCloseStart: null,
                        onOpenEnd: null,
                        onOpenStart: null,
                        outDuration: 200,
                        required: true,
                    }
                }}
            >
                <option value="select">State</option>
                <option value="Alabama">AL</option>
                <option value="Alaska">AK</option>
                <option value="Arizona">AZ</option>
                <option value="Arkansas">AR</option>
                <option value="California">CA</option>
                <option value="Colorado">CO</option>
                <option value="Connecticut">CT</option>
                <option value="Deleware">DE</option>
                <option value="Florida">FL</option>
                <option value="Georgia">GA</option>
                <option value="Hawaii">HI</option>
                <option value="Idaho">ID</option>
                <option value="Illinois">IL</option>
                <option value="Indiana">IN</option>
                <option value="Iowa">IA</option>
                <option value="Kansas">KS</option>
                <option value="Kentucky">KY</option>
                <option value="Louisiana">LA</option>
                <option value="Maine">ME</option>
                <option value="Maryland">MD</option>
                <option value="Massachusetts">MA</option>
                <option value="Michigan">MI</option>
                <option value="Minnesota">MN</option>
                <option value="Mississippi">MS</option>
                <option value="Missouri ">MO</option>
                <option value="Montana">MT</option>
                <option value="Nebraska">NE</option>
                <option value="Nevada">NV</option>
                <option value="New Hampshire">NH</option>
                <option value="New Jersey">NJ</option>
                <option value="New Mexico">NM</option>
                <option value="New York">NY</option>
                <option value="North Carolina">NC</option>
                <option value="North Dakota">ND</option>
                <option value="Ohio">OH</option>
                <option value="Oklahoma">OK</option>
                <option value="Oregon">OR</option>
                <option value="Pennsylvania">PA</option>
                <option value="Rhode Island">RI</option>
                <option value="South Carolina">SC</option>
                <option value="South Dakota">SD</option>
                <option value="Tennessee">TN</option>
                <option value="Texas">TX</option>
                <option value="Utah">UT</option>
                <option value="Vermont">VT</option>
                <option value="Virginia">VA</option>
                <option value="Washington">WA</option>
                <option value="West Virginia">WV</option>
                <option value="Wisconsin">WI</option>
                <option value="Wyoming">WY</option>
            </Select>
  )
}

const LeadsForm = props => {
    return (
      // <div id="form">
      <Formik 
        initialValues={{
          firstName: "",
          lastName: "",
          email: "",
          demo: "",
          phoneNumber: "",
          state: "select",
          zip: ""
        }}
        validate = {(values, meta) => {
          const errors = {};
    
          if (!values.email && meta.touched) {
            errors.email = "Email is required";
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
            errors.email = "Invalid email address";
          }

          if (!values.firstName && meta.touched) {
            errors.firstName = "First name is required"
          }

          if (!values.lastName && meta.touched) {
            errors.lastName = "Last name is required"
          }

          if (!values.phoneNumber && meta.touched) {
            errors.phoneNumber = "Phone number is required"
          }
          if (!values.zip && meta.touched) {
            errors.zip = "Zip Code is required"
          }
          
          return errors;
        }}
        onSubmit={(data, {setSubmitting}) => {
          setSubmitting(true);
          //make async call
          console.log("submitted: ", data);
          setSubmitting(false);
        }}
        >
          {({ values, errors, isSubmitting }) => (
            <div id="form">
            <CardPanel className="formCard">
              <Form>
                <div>
                  <div className="halves">
                    <MyTextInput name="firstName" type="text" label="First Name" />
                  </div>
                  <div className="halves">
                    <MyTextInput name="lastName" type="text" label="Last Name"  />
                  </div>
                </div>
                <div>
                  <div className="halves">
                    <MyTextInput name="email" type="email" label="Email" />
                  </div>
                  <div className="halves">
                    <div id="demoText">
                      <h6>Would you like a free demo?</h6>
                    </div>
                    <div id="radios">
                      <MyRadio name="demo" checked />                      
                    </div>
                  </div>
                </div>
                {/* phone number */}
                <div>
                  <div className="thirds">
                    <MyTextInput name="phoneNumber" type="tel" label="Phone Number" />
                  </div>
                  <div className="thirds" id="dropdown">
                    <MySelect name="state" error={errors.state}
                validate/>
                  </div>
                  <div className="thirds" id="zipDiv">
                  <MyTextInput name="zip" type="tel" label="Zip Code" />
                  </div>

                <Button 
                  id="submitButton"
                  node="button"
                  type="submit"
                  disabled={isSubmitting}
                  waves="light"
                  >Submit
                  <Icon right className="material-icon" id="icon">keyboard_arrow_right</Icon>
                </Button>
                  </div>
                  <Button onClick={() => {console.log(errors)}}>log</Button>

              </Form>
            </CardPanel>


              <pre>{JSON.stringify(values, null, 2)}</pre>
              <pre>{JSON.stringify(errors, null, 2)}</pre>
              </div>
          )}
        </Formik>
    )
};

export default LeadsForm;
