import React, { useLayoutEffect, useState } from "react";
import { CardPanel, Button } from "react-materialize";

import Header from "../Header/Header";
import MyFooter from "../Footer/Footer";

import "./productForm.css";
import products from "../../constants/products.js";


const ProductForm = props => {

    const [productIndex, setProductIndex] = useState(0);
    const [currentObj, setCurrentObj] = useState({});
    const [heading, setHeading] = useState();
    const [questionNumber, setQuestionNumber] = useState(1);
    const [iconNumber, setIconNumber] = useState(1);
    const [count, setCount] = useState(1);

    let url = props.match.params.product;

    useLayoutEffect(() => getCurrentProduct());

    const getCurrentProduct = () => {
        // productIndex = 0;
        for (let i = 0; i < products.products.length; i++) {
            if (url === products.products[i].name) {
                setProductIndex(i);
                setCurrentObj(products.products[i]);
            }
        }
        // return productIndex;
        console.log("this worked! " + currentObj.name);
        console.log("get product ran");
    }

    let radioQ;
    let radioH;
    let radioI;
    switch (count) {
        case 1:
            radioH = currentObj.header1
            radioQ = currentObj.questions1;
            radioI = currentObj.icons1;
            break;
        case 2:
            radioH = currentObj.header2;
            radioQ = currentObj.questions2;
            radioI = currentObj.icons2;
            break;

    }


   const Radios = () => {

        getCurrentProduct();
        console.log("this worked! " + productIndex);
        // console.log("this is test/ array number " + productIndex)
        // let currentObj = products.products[productIndex];
        // let indexNum = 0;
        radioQ.map((item) => {
            // indexNum = currentObj.questions1.indexOf(item);
            return (
                <div key={item}className="customRadio">
                    {/* <CardPanel> */}
                    <input
                        className="with-gap" type="radio" name="product"
                        id={currentObj.name} value="product" />
                    <label htmlFor='product'>
                        <img className="radioImage hoverable"
                            src={radioI}
                            alt={item} />
                    </label>
                    <p>{item}</p>
                    {/* </CardPanel> */}
                </div>
            );
        });
        // setHeading(currentObj.header1);
        // return currentQuestion;
    };

    // use state once submit his hit, to slide in the second set of questions

    return (
        <div>
            <div className="content-wrap" >
                <Header
                    partnerLink={"/partners"}
                    productLink={"/#productHome"}
                />
                <div className="productFormContainer">
                    <CardPanel className="formCard">
                        <h2>{radioH}</h2>
                        <div id="radioDiv">
                            <Radios />
                        </div>
                        <Button id="productBttn" onClick={() => setCount(count + 1)}> Next </Button>
                    </CardPanel>
                </div>
            </div>
            <MyFooter />
        </div>
    )
};

export default ProductForm;
